import re

medicine = ""

with open("Day 19/input.txt", "r") as fp:
    replacements = [line.strip().split(" => ") for line in fp.readlines()]

distinct_molecules = set()

for key, replacement in replacements:
    matches = re.finditer(key, medicine)
    for match in matches:
        molecule = list(medicine)
        
        start, end = match.span()
        del molecule[start:end]
        molecule.insert(start, replacement)

        molecule_string = "".join(molecule)
        distinct_molecules.add(molecule_string)

print(len(distinct_molecules))
